const modals = () => {
    function bindModals(triggerSelector, modalSelector, closeSelector) {
        const trigger = document.querySelectorAll(triggerSelector),
            modal = document.querySelector(modalSelector),
            close = document.querySelector(closeSelector);

        trigger.forEach(item => {
            item.addEventListener('click', (e) => {
                if (e.target) {
                    e.preventDefault();
                }
                modal.style.display = "block";
                document.body.style.overflow = "hidden";
            });
        });

        close.addEventListener('click', () => {
            modal.style.display = "none";
            document.body.style.overflow = "";
        });

        modal.addEventListener('click', (e) => {
            if (e.target === modal) {
                modal.style.display = "none";
                document.body.style.overflow = "";
            }
        });
    }

    bindModals('.main__form', '.popup_engineer', '.popup_engineer .popup_close');
};

window.addEventListener('DOMContentLoaded', () => {
    modals();
});

$(function () {


    $('.tabs__inner-item').on('click', function (e) {
        e.preventDefault();


        $('.tabs__inner-item').removeClass('tabs__inner-item--active');
        $('.tabs__content-item').removeClass('tabs__content-item--active');

        $(this).addClass('tabs__inner-item--active');
        $($(this).attr('href')).addClass('tabs__content-item--active');
    });


    $('.questions__item').on('click', function (e) {
        e.preventDefault();
        $('.questions__item').removeAttr('questions__item--cloce');
        $(this).toggleClass('questions__item--cloce');
        $($(this).attr('href')).toggleClass('questions__item--cloce');
    });

});
